import React, {Component} from 'react';
import AOS from 'aos';
import MailchimpSubscribe from "react-mailchimp-subscribe"

import {Poem, Stanza} from './Poem'
import Team from './Team'
import Page from './Page'
import About from './About'
import Tickets from './Tickets'
import Sponsors from './Sponsors'
import Questions from './Questions'

import './App.css';
import './aos.css';
import 'aos/dist/aos.css';

import nbLink from './images/NBBlack.png';

class App extends Component {
  componentDidMount() {
    AOS.init({offset: 0, delay: 0, duration: 300})
  }

  render() {
    return (
      <>
        <Landing/>
        <Sponsors/>
        <About/>
        <Tickets/>
        <Questions/>
        <Team/>
        <Footer/>
      </>
    );
  }
}

class Landing extends Component {
  render() {
    return (
      <Page>
        <div className="Landing">
        <NewBuildings/>
          <h1 className="Title">
            MAGDALEN BALL
          </h1>
        </div>
      </Page>
    );
  }
}


class NewBuildings extends Component {
  render() {
    return (
      <img className="NewBuildings" src={nbLink} alt={"New Buildings"}/>
    );
  }
}



class Footer extends Component {
  render() {
    return (
      <div className="Footer row justify-content-center">
        <div className="col">
          2019 Magdalen Ball Committee
        </div>
        <div className="col">
          Magdalen College, High Street, Oxford, OX1 4AU
        </div>
        <div className="col Subscribe">
          <MailchimpSubscribe
            url={"https://magdalenball.us4.list-manage.com/subscribe/post?u=391e3e7605c9abbf41de699dd&amp;id=4715bb18dc"}
            render={({ subscribe, status, message }) => (
              <SubmitForm
                status={status}
                message={message}
                onValidated={formData => subscribe(formData)}
              />
            )}
          />
        </div>
      </div>
    );
  }
}



const SubmitForm = ({ status, message, onValidated }) => {
  let email, name;
  const submit = () =>
    email &&
    name &&
    email.value.indexOf("@") > -1 &&
    onValidated({
      EMAIL: email.value,
      NAME: name.value
    });

  return (
    <div
      style={{
        background: "#efefef",
        borderRadius: 2,
        padding: 10,
        display: "inline-block"
      }}
    >
      {status === "sending" && <div style={{ color: "blue" }}>sending...</div>}
      {status === "error" && (
        <div
          style={{ color: "red" }}
          dangerouslySetInnerHTML={{ __html: message }}
        />
      )}
      {status === "success" && (
        <div
          style={{ color: "green" }}
          dangerouslySetInnerHTML={{ __html: message }}
        />
      )}
      <input
        style={{ fontSize: "2em", padding: 5 }}
        ref={node => (name = node)}
        type="text"
        placeholder="Your name"
      />
      <br />
      <input
        style={{ fontSize: "2em", padding: 5 }}
        ref={node => (email = node)}
        type="email"
        placeholder="Your email"
      />
      <br />
      <button style={{ fontSize: "2em", padding: 5 }} onClick={submit}>
        Submit
      </button>
    </div>
  );
};


export default App;
