import './App.css';
import './aos.css';
import 'aos/dist/aos.css';

import dafyddLink from './images/Dafydd.jpg';


class App extends Component {
  componentDidMount() {
    AOS.init({offset: 0, delay: 0, duration: 300})
  }

  render() {
    return (
      <>
        <Landing/>

        <Team>
          <TeamRow breaks="col-12 col-md-9">
            <TeamMember name="Arthur James" title="CHAIR" email="arthur@magdalenball.com" breaks="col-4"/>
            <TeamMember name="Mark BÃ¤cker" title="VICE-CHAIR" email="mark@magdalenball.com" breaks="col-4"/>
            <TeamMember name="Toby Morrison" title="TREASURER" email="toby@magdalenball.com" breaks="col-4"/>
          </TeamRow>
        </Team>
      </>
    );
  }
}

class Landing extends Component {
  render() {
    return (
      <div className="Landing">
        <div className="TitleContainer">
          <h1 className="Title">
            MAGDALEN BALL
          </h1>
          <div className="Tagline">
            Amazing tagline about the illustrious 287th ball.
          </div>
        </div>
      </div>
    );
  }
}

export default App;
