import React, {Component} from 'react';

import './Tickets.css';
import Page from './Page'


class Tickets extends Component {
  render() {
    return (
      <div className="Tickets row col-12">
        <Ticket title="STANDARD" price="£100">
          You and a guest will experience Cambridge’s best Ball, an unrivalled showcase of revelry—unlimited food and drink, a stunning fireworks display, and a myriad of entertainments from chart-topping stars to world-class comedians to the finest classical and jazz musicians.
        </Ticket>
        <Ticket title="VIP" price="£120">
          You and a guest will experience Cambridge’s best Ball, an unrivalled showcase of revelry—unlimited food and drink, a stunning fireworks display, and a myriad of entertainments from chart-topping stars to world-class comedians to the finest classical and jazz musicians.
          You and a guest will experience Cambridge’s best Ball, an unrivalled showcase of revelry—unlimited food and drink, a stunning fireworks display, and a myriad of entertainments from chart-topping stars to world-class comedians to the finest classical and jazz musicians.
        </Ticket>
        <Ticket title="PUNTING" price="£110">
          One of the most sought after tickets to be had in May Week. You and a guest will experience Cambridge’s best Ball, an unrivalled showcase of revelry—unlimited food and drink, a stunning fireworks display, and a myriad of entertainments from chart-topping stars to world-class comedians to the finest classical and jazz musicians.
        </Ticket>
      </div>
    );
  }
}


class Ticket extends Component {
  render() {
    return (
      <div className="TicketContainer col-md-4 col-sm-10 col-xs-11">
        <div className="Ticket">
          <div className="TicketTitle">
            {this.props.title}
          </div>
          {this.props.buffer &&
            <div className="TicketBuffer">
              {this.props.buffer}
            </div>
          }
          <div className="TicketInfo">
            {this.props.children}
          </div>
          <div className="TicketPrice">
            {this.props.price}
          </div>
        </div>
      </div>
    );
  }
}

export default Tickets
