import React, {Component} from 'react';

import './About.css';

import dafyddLink from './images/Dafydd.jpg';

class About extends Component {
  render() {
    return (
      <div className="About justify-content-center">
        <AboutParagraph img={dafyddLink} start={true}>
          Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
        </AboutParagraph>
        <AboutParagraph img={dafyddLink} flip={true}>
          Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
        </AboutParagraph>
        <AboutParagraph img={dafyddLink} end={true}>
          Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
        </AboutParagraph>
        <Timeline size={3}/>
      </div>
    );
  }
}


class AboutParagraph extends Component {
  render() {
    return (
      <div className="AboutParagraph row col-lg-11 justify-content-center">
        {!this.props.flip && <img className="AboutImg col-5" src={this.props.img} alt="About"/>}
        {!this.props.flip && <TimelineSection start={this.props.start} end={this.props.end}/>}
        <div className="AboutText col-5">
          {this.props.children}
        </div>
        {this.props.flip && <TimelineSection start={this.props.start} end={this.props.end}/>}
        {this.props.flip && <img className="AboutImg col-5" src={this.props.img} alt="About"/>}
      </div>
    );
  }
}

class TimelineSection extends Component {
  componentDidMount() {
    this.updateCanvas();
  }
  updateCanvas() {
    const canvas = this.refs.canvas
    const ctx = canvas.getContext('2d');
    const width = canvas.offsetWidth;
    const height = canvas.offsetHeight;
    console.log(width, height);
    canvas.width = width * window.devicePixelRatio;
    canvas.height = height * window.devicePixelRatio;
    ctx.scale(window.devicePixelRatio, window.devicePixelRatio);


    ctx.fillStyle = "#fff4f2";
    ctx.strokeStyle = "#21190c";
    ctx.lineWidth = 3;
    ctx.beginPath();
    ctx.ellipse(width/2, height/2, 12, 12, 0, 0, 2*Math.PI)
    ctx.fill();
    ctx.ellipse(width/2, height/2, 12, 12, 0, 0, 2*Math.PI)
    ctx.stroke();
    if (this.props.start) {ctx.fillRect(0,0,width,height/2);}
    if (this.props.end) {ctx.fillRect(0,height/2,width,height);}
  }
  render() {
    return (
      <div className="TimelineSection col-1">
        <canvas ref="canvas" className="TimelineSectionCanvas"/>
      </div>
    );
  }
}



class Timeline extends Component {
  componentDidMount() {
    this.updateCanvas();
  }
  updateCanvas() {
    const canvas = this.refs.canvas
    const ctx = canvas.getContext('2d');
    const width = canvas.offsetWidth;
    const height = canvas.offsetHeight;
    canvas.width = width * window.devicePixelRatio;
    canvas.height = height * window.devicePixelRatio;
    ctx.scale(window.devicePixelRatio, window.devicePixelRatio);

    ctx.strokeStyle = "#21190c";
    ctx.lineWidth = 3;
    ctx.moveTo(width*0.5, 0);
    ctx.lineTo(width*0.5, height);
    ctx.stroke();
  }
  render() {
    return (
      <canvas ref="canvas" className="Timeline"/>
    );
  }
}


export default About
