import React, {Component} from 'react';
import AOS from 'aos';
import nextId from "react-id-generator";
import {Poem, Stanza} from './Poem'
import Team from './Team'
import Page from './Page'
import Tickets from './Tickets'
import Sponsors from './Sponsors'

import './App.css';
import './aos.css';
import 'aos/dist/aos.css';

import dafyddLink from './images/Dafydd.jpg';
import nbLink from './images/NBBlack.png';

class App extends Component {
  componentDidMount() {
    AOS.init({offset: 0, delay: 0, duration: 300})
  }

  render() {
    return (
      <>
        <Landing/>
        <Sponsors/>
        <About/>
        <Tickets/>
        <Team/>
      </>
    );
  }
}

class Landing extends Component {
  render() {
    return (
      <Page>
        <div className="Landing">
        <NewBuildings/>
          <h1 className="Title">
            MAGDALEN BALL
          </h1>
        </div>
      </Page>
    );
  }
}


class About extends Component {
  render() {
    return (
      <div className="About justify-content-center">
        <AboutParagraph img={dafyddLink}>
          Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
        </AboutParagraph>
        <AboutParagraph img={dafyddLink} flip={true}>
          Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
        </AboutParagraph>
        <AboutParagraph img={dafyddLink}>
          Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
        </AboutParagraph>
        <Timeline size={3}/>
      </div>
    );
  }
}

class AboutParagraph extends Component {
  render() {
    return (
      <div className="AboutParagraph row col-lg-11 justify-content-center">
        {!this.props.flip && <img className="AboutImg col-5" src={this.props.img} alt="About"/>}
        {!this.props.flip && <AboutBar/>}
        <div className="AboutText col-5">
          {this.props.children}
        </div>
        {this.props.flip && <AboutBar/>}
        {this.props.flip && <img className="AboutImg col-5" src={this.props.img} alt="About"/>}
      </div>
    );
  }
}

class AboutBar extends Component {
  render() {
    return (
      <div className="AboutBar col-1">
        <TimelineElem/>
      </div>
    );
  }
}


class TimelineElem extends Component {
  componentDidMount() {
    this.updateCanvas();
  }
  updateCanvas() {
    const canvas = this.refs.canvas
    const ctx = canvas.getContext('2d');
    canvas.width  = canvas.clientWidth;
    canvas.height = canvas.clientHeight;

    ctx.fillStyle = "#21190C";
    ctx.strokeStyle = "#21190C";
    ctx.lineWidth = 3;
    ctx.beginPath();
    ctx.ellipse(canvas.width/2, canvas.height/2, 12, 12, 0, 0, 2*Math.PI)
    ctx.fill();
  }
  render() {
    return (
      <canvas ref="canvas" className="TimelineElem"/>
    );
  }
}



class Timeline extends Component {
  componentDidMount() {
    this.updateCanvas();
  }
  updateCanvas() {
    const canvas = this.refs.canvas
    const ctx = canvas.getContext('2d');
    canvas.width  = canvas.offsetWidth;
    canvas.height = canvas.offsetHeight;

    ctx.strokeStyle = "#21190C";
    ctx.lineWidth = 3;
    ctx.moveTo(canvas.width*0.5, 0);
    ctx.lineTo(canvas.width*0.5, canvas.height);
    ctx.stroke();
  }
  render() {
    return (
      <canvas ref="canvas" className="Timeline"/>
    );
  }
}


class NewBuildings extends Component {
  render() {
    return (
      <img className="NewBuildings" src={nbLink} alt={"New Buildings"}/>
    );
  }
}


export default App;
