import React, {Component} from 'react';
import AOS from 'aos';

import {Poem, Stanza} from './Poem'
import Team from './Team'
import Page from './Page'
import About from './About'
import Tickets from './Tickets'
import Sponsors from './Sponsors'
import Questions from './Questions'

import './App.css';
import './aos.css';
import 'aos/dist/aos.css';

import nbLink from './images/NBBlack.png';

class App extends Component {
  componentDidMount() {
    AOS.init({offset: 0, delay: 0, duration: 300})
  }

  render() {
    return (
      <>
        <Landing/>
        <Sponsors/>
        <About/>
        <Tickets/>
        <Questions/>
        <Team/>
        <Footer/>
      </>
    );
  }
}

class Landing extends Component {
  render() {
    return (
      <Page>
        <div className="Landing">
        <NewBuildings/>
          <h1 className="Title">
            MAGDALEN BALL
          </h1>
        </div>
      </Page>
    );
  }
}


class NewBuildings extends Component {
  render() {
    return (
      <img className="NewBuildings" src={nbLink} alt={"New Buildings"}/>
    );
  }
}



class Footer extends Component {
  render() {
    return (
      <div className="Footer row justify-content-center">
        <div className="col">
          2019 Magdalen Ball Committee
        </div>
        <div className="col">
          Magdalen College, High Street, Oxford, OX1 4AU
        </div>
        <div className="col">
          <div id="mc_embed_signup">
            <form action="https://magdalenball.us4.list-manage.com/subscribe/post?u=391e3e7605c9abbf41de699dd&amp;id=4715bb18dc" method="post" id="mc-embedded-subscribe-form" name="mc-embedded-subscribe-form" className="validate" target="_blank" novalidate>
              <div id="mc_embed_signup_scroll">
                <div className="mc-field-group">
          	      <label for="mce-EMAIL">Email Address</label>
          	      <input type="email" value="" name="EMAIL" className="required email" id="mce-EMAIL"/>
                </div>
          	    <div id="mce-responses" className="clear">
          		    <div className="response" id="mce-error-response" style={{display: "none"}}></div>
          		    <div className="response" id="mce-success-response" style={{display: "none"}}></div>
          	    </div>
                <div style="position: absolute; left: -5000px;" aria-hidden="true">
                  <input type="text" name="b_391e3e7605c9abbf41de699dd_4715bb18dc" tabindex="-1" value=""/>
                </div>
                <div className="clear">
                  <input type="submit" value="Subscribe" name="subscribe" id="mc-embedded-subscribe" className="button"/>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    );
  }
}





export default App;
