import React, {Component} from 'react';

import './About.css';

import dafyddLink from './images/Dafydd.jpg';

class About extends Component {
  render() {
    return (
      <div className="About justify-content-center">
        <AboutParagraph img={dafyddLink} start={true} title="ABOUT">
        Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
        Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
        </AboutParagraph>
        <AboutParagraph img={dafyddLink} flip={true} title="ENTERTAINMENT">
          Magdalen is famed for booking some of the biggest acts around. The Rolling Stones (pictured playing at the '64 Ball), Fleetwood Mac, Pink Floyd and more recently Pharrell Williams have all played to sold-out crowds in Magdalen's beautiful quads. Watch our Facebook page for announcements on this year's headline act!
        </AboutParagraph>
        <AboutParagraph img={dafyddLink} end={true} title="FOOD & DRINK">
          Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
        </AboutParagraph>
        <AboutParagraph img={dafyddLink} end={true} title="CHARITY">
          Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
        </AboutParagraph>
      </div>
    );
  }
}


class AboutParagraph extends Component {
  render() {
    return (
      <div className="AboutParagraph row col-lg-11 justify-content-center">
        {!this.props.flip && <img className="AboutImg col-5" src={this.props.img} alt="About"/>}
        {!this.props.flip && <div className="col-1"/>}
        <div className="AboutData col-5">
          <div className="AboutTitle">
            {this.props.title}
          </div>
          <div className="AboutText">
            {this.props.children}
          </div>
        </div>
        {this.props.flip && <div className="col-1"/>}
        {this.props.flip && <img className="AboutImg col-5" src={this.props.img} alt="About"/>}
      </div>
    );
  }
}


export default About
